\begin{code}
#!/usr/bin/python
import psycopg2
conn = psycopg2.connect(database="foo")
cur = conn.cursor()
cur.execute("SELECT count(*) FROM foo_table")
num_rows = cur.fetchone()[0]
\end{code}

