import math

def predict(s):
    pred1 = s * (1 - 0.1)
    pred2 = s * (1 + 0.1)
    return np.random.uniform(pred1, pred2)

def f(s, t):
    return math.sqrt(t * (1 - t) * np.random.normal(0, 1) ** 2)

def max_variance(predict_func, f, start, stocks, t):
    stocks = np.array(stocks)
    return np.var(
        (stocks * stocks.T) @ (predict_func(s) - f(s, t))
        for s in range(start, stocks.shape[0])
    )

def max_expected_profit(predict_func, f, start, stocks, t):
    stocks = np.array(stocks)
    stocks_sum = stocks.sum(axis=0)
    return np.max(
        np.sum(
            (stocks * stocks.T) @ (predict_func(s) - f(s, t))
            for s in range(start, stocks.shape[0])
        )
        - stocks_sum * np.sum(predict_func(s) - f(s, t))
        for s in range(start, stocks.shape[0])
    )

