\begin{code}
class TrieNode:
    def __init__(self, value):
        self.value = value
        self.children = {}

    def insert(self, value):
        if not self.children:
            self.children = {}
        for c in value:
            if not self.children:
                self.children[c] = TrieNode(None)
                self.children[c].parent = self
            else:
                self.children[c] = self.children[c].insert(value[c:])

    def find(self, value):
        if not self.children:
            return None
        for c in value:
            try:
                self.children[c].parent
            except KeyError:
                return None
            else:
                self.children[c].parent = self
                self.children[c] = self.children[c].find(value[c:])
        return self

    def get_values(self):
        values = []
        for c in self.children:
            values.extend(self.children[c].get_values())
        values.append(self.value)
        return values

    def count_values(self):
        if not self.children:
            return 1
        value_count = 0
        for c in self.children:
            value_count += self.children[c].count_values()
            value_count += self.children[c].count_values()
        value_count += 1
        return value_count

class Trie(object):
    def __init__(self):
        self.root = TrieNode('')

    def insert(self, value):
        self.root.insert(value)

    def find(self, value):
        return self.root.find(value)

    def count_values(self):
        return self.root.count_values()

    def get_values(self):
        return self.root.get_values()

