    location: location
  }, function(results, status) {
    if (status == google.maps.GeocoderStatus.OK) {
      var nearest = results[0].address_components;
      if (nearest["locality"]) {
        nearest = nearest["locality"][0];
        var url = "http://maps.googleapis.com/maps/api/geocode/json?latlng="+
