\begin{code}
import requests

params = {
    'appid': '230',
    'name': 'Counter-Strike: Global Offensive',
    'platform': 'Steam Web API',
    'cc': 'US',
    'format': 'json',
    'fields': 'platform,name,appid,release_date'
}

url = 'https://api.steampowered.com/ISteamApps/GetAppList/v2/'

r = requests.get(url, params=params)

data = r.json()

for key in data:
    if key == 'apps':
        for item in data[key]:
            print (item['name'])
            print (item['release_date'])
\end{code}

